query TrovesByAccount($account: Bytes!) {
  troves(where: {borrower: $account}) {
    id
    troveId
    borrower
    debt
    deposit
    stake
    interestRate
    createdAt
    closedAt
    collateral {
      id
      token {
        symbol
        name
      }
      minCollRatio
      collIndex
    }
  }
}

query TrovesCount($id: ID!) {
  borrowerInfo(id: $id) {
    troves
    trovesByCollateral
  }
}

query TroveById($id: ID!) {
  trove(id: $id) {
    id
    troveId
    borrower
    debt
    deposit
    stake
    interestRate
    createdAt
    closedAt
    collateral {
      id
      token {
        symbol
        name
      }
      minCollRatio
      collIndex
    }
  }
}
